import * as React from 'react';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import WebImage from '../componenet/WebImage';
import FormControl from '@mui/material/FormControl';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import validator from 'validator'
import { useState } from 'react';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Checkbox from '@mui/material/Checkbox';
import { Theme, useTheme } from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import Chip from '@mui/material/Chip';
import classes from './RegisterUserDetail.module.css'



const theme = createTheme({palette:
    {
        primary:{main:"#008000"},
        // secondary:{main:"yellow"}
    },
});

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};


function getStyles(category, personCategory, theme) {
  return {
    fontWeight:
      personCategory.indexOf(category) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

function getStyles2(location, personLocation, theme) {
  return {
    fontWeight:
      personLocation.indexOf(location) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

function getStyles3(time, personTimePeriod, theme) {
  return {
    fontWeight:
      personTimePeriod.indexOf(time) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

function getStyles4(time, personTimePeriod, theme) {
  return {
    fontWeight:
      personTimePeriod.indexOf(time) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}


const locations = [
  'Asia',
  'Australia and/or New Zealand',
  'Pacific Islands',
  'Europe',
  'United States and/or Canada',
  'Latin America and/or Caribbean',
  'Middle East',
  'N/A',
];


const periods = [
  '19th Century',
  '20th Century',
  '21st Century',
  'N/A',
];

const topics = [
  'Aesthetics',
  'Anti-Mormonism',
  'Biography',
  'Childhood/Youth',
  'Church Membership',
  'Church of Jesus Christ of Latter-day Saints',
  'Colonialism/imperialism',
  'Community of Christ (formerly Reorganized Church of Jesus Christ of Latter Day Saints)',
  'Critical Race Studies',
  'Creative Writing (Fiction/Nonfiction/Poetry/etc.)',
  'Cultural History',
  'Demography',
  'Disability Studies',
  'Drama',
  'Ecclesiology',
  'Economics',
  'Ethics',
  'Family structure',
  'Film',
  'Folklore/Storytelling',
  'Food',
  'Gender/Femininity/Masculinity/Sexuality',
  'Globalization',
  'Healing',
  'Interfaith/Interreligious Relations/Dialogue',
  'Literature',
  'Material Culture',
  'Missions/Missiology',
  'Motherhood',
  'Music',
  'Other Mormon Traditions (AUB/Bickertonite/FLDS/Strangite/etc.)',
  'Performance',
  'Philosophy',
  'Psychology',
  'Politics/Political Issues/Political Engagement',
  'Popular Culture',
  'Race/Ethnicity',
  'Ritual Studies',
  'Sacred Space',
  'Scripture',
  'Social History',
  'Social Justice',
  'Sociology of Religion',
  'Technical Communication',
  'Temples',
  'Theology',
  'Translation',
  'Visual Culture',
  'Womens History',
  'N/A',
];

const methods = [
  'Anthropology',
  'Area Studies',
  'Art History',
  'Creative Writing',
  'Disability Studies',
  'Economics',
  'Ethnography',
  'Ethnohistory',
  'Gender Studies',
  'Genealogy',
  'Geography',
